global gx = 0 gy = 0;global readingX;global counterreadingX = true;counter = 0;function value = userInput()  global gx gy readingX;  if (readingX)    value = gx;    readingX = false;  else     value = gy;    readingX = true;  endifendfunctionfunction userOutput(value)  global counter;  if (value == 1)    counter += 1;  endifendfunctionmem = csvread("day_19.in");for y = 0:49  for x = 0:49    gx = x;    gy = y;    run(mem,@userInput,@userOutput);  endfor  disp(num2str(y));endfordisp(["answer ", num2str(counter)])